"use strict";lazyload();let path=window.location.href;const loading_show=()=>{const loading_icon=document.getElementById("loading_icon"),fullWrapper=document.getElementById("fullWrapper");loading_icon.classList.remove("prop_hide"),fullWrapper.classList.add("el__deactivate")};loading_show();const loading_hide=()=>{const loading_icon=document.getElementById("loading_icon"),fullWrapper=document.getElementById("fullWrapper");loading_icon.classList.add("prop_hide"),fullWrapper.classList.remove("el__deactivate")};if(loading_hide(),"/order_manage/"==path){const manageMenuConfirm=()=>{const manageMenu=document.getElementById("manageMenu");manageMenu.addEventListener("click",()=>{const answer=confirm("この画面から離れますが問題ありませんか？");answer&&(window.location.href="/manage/menu/")})};manageMenuConfirm()}if(path.indexOf("detail")>-1){let cartPlusMinus;(()=>{let cartNum=document.getElementById("cartNum"),NumVal=cartNum.value,num=parseInt(NumVal);const cartMinus=document.getElementById("cartMinus"),cartPlus=document.getElementById("cartPlus");cartMinus.addEventListener("click",()=>{num>1&&(num-=1,cartNum.value=num)}),cartPlus.addEventListener("click",()=>{num+=1,cartNum.value=num})})()}