"use strict";let path=window.location.href;if("/order_manage/"==path){const manageMenuConfirm=()=>{const manageMenu=document.getElementById("manageMenu");manageMenu.addEventListener("click",()=>{const answer=confirm("この画面から離れますが問題ありませんか？");answer&&(window.location.href="/manage/menu/")})};manageMenuConfirm()}let cartPlusMinus=()=>{let cartNum=document.getElementById("cartNum"),NumVal=cartNum.value,num=parseInt(NumVal);const cartMinus=document.getElementById("cartMinus"),cartPlus=document.getElementById("cartPlus");cartMinus.addEventListener("click",()=>{num>1&&(num-=1,cartNum.value=num)}),cartPlus.addEventListener("click",()=>{num+=1,cartNum.value=num})};cartPlusMinus();